import tarfile
import os

class Event:

    def __init__(self, name)):
        self.name = name

    def get_topfiles():
        """Return the top n events."""
        tar = tarfile.open("events.tar.gz")
        tar.extractall(path="../")

        files = os.listdir(".txt")

        return [f for f in files if len(f.name) == 100]

def main():
    events = Event.get_topfiles()

    for event in events:
        file_path = os.path.join(".txt", event.name)

        if os.path.isfile(file_path):
            with open(file_path) as f:
                print(f.read())

if __name__ == "__main__":
    main()

